<?php
function page_links_generate($record_count,$link_count,$active_page,$href,$class_name='',$active_link_class_name='',$previous_msg='',$next_msg=''){	/*	$record_count			 = Toplam kayıt sayısı.	$link_count			 = Her sayfada gösterilmesi planlanan kayıt sayısı.	$active_page			 = O anda aktif olan sayfa numarası  (1 den başlar)	$$href				 = Linkerin hedefi  (index.php?  veya  $index.php?id=15)	$class_name			 = Link'lere uygulanacak sitilin adı	$active_link_class_name	 = O anda aktif olan link'in sitilin adı.	$previous_msg			 = 'Bir önceki sayfa' link'inin kullanıcı açıklaması	$next_msg				 = 'Bir sonraki sayfa' link'inin kullanıcı açıklaması	*/	echo '<div class="list_pager"><div>';	$page_count=ceil($record_count/$link_count);		if(($active_page)<2)		echo '<a class="'.$class_name.' '.$active_link_class_name.'" href="#">&lt;</a> ';	else		echo '<a class="'.$class_name.'" href="'.$href.''.($active_page-1).'" title="'.$previous_msg.'">&lt;</a> ';	$link='';	for($i=$active_page-1;$i>0;$i--)	{			$link='<a class="'.$class_name.'" href="'.$href.''.$i.'">'.$i.'</a> '.$link;		if($i-1>2)		{			$link= '<span>...</span>'.$link;$i=2;		}	}	$link=$link.'<a class="'.$class_name.' '.$active_link_class_name.'" href="#">'.$active_page.'</a> ';	for($i=$active_page+1;$i<=$page_count;$i++)	{		$link=$link.'<a class="'.$class_name.'" href="'.$href.''.$i.'">'.$i.'</a> ';		if($page_count-$i>3)		{			$link= $link.'<span>...</span>';$i=$page_count-1;		}	}	echo $link;	if($active_page>=$page_count)		echo '<a class="'.$class_name.' '.$active_link_class_name.'" href="#">&gt;</a>';	else		echo '<a class="'.$class_name.'" href="'.$href.''.($active_page+1).'" title="'.$next_msg.'">&gt;</a>';		echo '</div></div>';}
?>
